import React, { Component } from "react";
import './styles/tailwind.css';


/*---------------------------------------------------------------
            Définition des template de classes communs 
------------------------------------------------------------------*/

var btnTemplate     = "outline-none px-6 py-2  uppercase font-semibold tracking-wider text-sm mr-2 my-2 border-2 ";
var titleTemplate   = "uppercase font-semibold tracking-wider my-1 text-gray-900 ";

/*---------------------------------------------------------------
                        Composant HEADER
------------------------------------------------------------------*/
function HeaderMain(props){
  var logo;
  if(props.logo!==""){
    logo = <img src={props.logo} alt="brand logo"/>
  }
  return(
    <header className="px-8 py-4 mb-4 border-t-4 border-brandColor-700 shadow bg-white">
      <div>
        {logo}
      </div>
      <span className="uppercase font-semibold tracking-wider text-gray-900 text-sm">{props.text}</span>
    </header>
  )
}

function HeaderTitleFull(props){

  return(
    <div className="px-8 py-4 mb-4 bg-gray-300 text-gray-700">
        {props.children}
    </div>
  )
}

/*---------------------------------------------------------------
                          cards
------------------------------------------------------------------*/

function Card(props){
  return(
    <div className="bg-white shadow-lg rounded-lg w-full my-10 pb-4 overflow-hidden">
        {props.children}
    </div>
  )
}

function CardImageLeft(props){

    // on offre le choix de donner une taille dans les attirbuts. Par défaut la card prend la totalité de l'élement
    let cardSize = 'w-full';
    if(props.size==='md'){
        cardSize = 'w-4/5';
    }
    else if(props.size==='lg'){
        cardSize = 'w-5/6';
    }


    return(
        <div className={"flex-none mr-10 lg:h-150 overflow-hidden bg-white shadow-lg rounded-lg my-10 "+ cardSize}>
            <section className="h-full w-full items-center lg:flex">
                <img className="h-40 w-full lg:h-full object-cover lg:w-1/2" src={process.env.PUBLIC_URL + '/img/unsplash/resized/'+props.image}/>

                <div className="lg:w-1/2 p-10 lg:h-full overflow-y-scroll">
                    {props.children}
                </div>
            </section>
        </div>



  )
}
