  return(
    <section className="items-center lg:flex bg-white shadow-lg rounded-lg my-10 overflow-hidden">
        <img className="h-40 w-full object-cover lg:hidden" src={process.env.PUBLIC_URL + '/img/unsplash/resized/'+props.image}/>

        <div className="lg:w-1/2 p-10">
            {props.children}
        </div>

        <img className="hidden object-cover lg:w-1/2 lg:block -p-10" src={process.env.PUBLIC_URL + '/img/unsplash/resized/'+props.image}/>

    </section>
  )
}

function CardImageFull(props){
