import React, { Component } from "react";
import ReactDOM from "react-dom";
import './styles/tailwind.css';


/*---------------------------------------------------------------
            Définition des template de classes communs 
------------------------------------------------------------------*/

var btnTemplate     = "outline-none px-6 py-4  uppercase font-semibold tracking-wider text-sm ";
var titleTemplate   = "uppercase font-semibold tracking-wider text-gray-900"

/*---------------------------------------------------------------
                        Composant HEADER
------------------------------------------------------------------*/
function Header(props){
  var logo;
  if(props.logo!=""){
    logo = <img src={props.logo}/>
  }
  return(
    <header className="px-8 py-4 mb-4 border-t-4 border-brandColor-700 shadow bg-white">
      <div>
        {logo}
      </div>
      <span className="uppercase font-semibold tracking-wider text-gray-900 text-sm">{props.text}</span>
    </header>
  )
}

/*---------------------------------------------------------------
                        titles
------------------------------------------------------------------*/
function H1(props){
  return(
    <h1
      className = { titleTemplate + "bg-brandColor-200 text-brandColor-800 border-4 border-brandColor-200 hover:bg-brandColor-300 rounded hover:border-brandColor-300" }>
      {props.text}
    </h1>
  )
}

/*---------------------------------------------------------------
                          Buttons
------------------------------------------------------------------*/
function BtnMainFilled(props){
  return(
    <button
      className = { btnTemplate + "bg-brandColor-200 text-brandColor-800 border-4 border-brandColor-200 hover:bg-brandColor-300 rounded hover:border-brandColor-300" }
      onClick   = {()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

// boutton avec fond transparent border 4
function BtnMainStroked(props){
  return(
    <button
      className = { btnTemplate + "bg-transparent text-brandColor-800 border-4 border-brandColor-200 hover:bg-brandColor-200 rounded" }
      onClick   = {()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

function BtnMainPillFilled(props){
  return(
    <button
      className = { btnTemplate + "bg-brandColor-200 text-brandColor-800 border-4 border-brandColor-200 hover:bg-brandColor-300 rounded-full hover:border-brandColor-300" }
      onClick   = {()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

// boutton avec fond transparent border 4
function BtnMainPillStroked(props){
  return(
    <button
      className = { btnTemplate + "bg-transparent text-brandColor-800 border-4 border-brandColor-200 hover:bg-brandColor-200 rounded-full" }
      onClick   = {()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

function BtnAltFilled(props){
  return(
    <button
      className={btnTemplate+"bg-gray-200 hover:bg-gray-300 border-4 border-gray-200 hover:border-gray-300 text-gray-800 rounded"}
      onClick={()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}


function BtnAltStroked(props){
  return(
    <button
      className={btnTemplate+"bg-transparent hover:bg-gray-300 border-4 border-gray-300 text-gray-800 rounded"}
      onClick={()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

function BtnAltPillFilled(props){
  return(
    <button
      className={btnTemplate+"bg-gray-200 hover:bg-gray-300 border-4 border-gray-200 hover:border-gray-300 text-gray-800 rounded-full"}
      onClick={()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}


function BtnAltPillStroked(props){
  return(
    <button
      className={btnTemplate+"bg-transparent hover:bg-gray-300 border-4 border-gray-300 text-gray-800 rounded-full"}
      onClick={()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

/*---------------------------------------------------------------
                          Buttons
------------------------------------------------------------------*/

export {
    Header,
    BtnMainFilled,
    BtnMainStroked,
    BtnMainPillFilled,
    BtnMainPillStroked,
    BtnAltFilled,
    BtnAltStroked,
    BtnAltPillFilled,
    BtnAltPillStroked
}
