import React from 'react';
import CameraPhoto, { FACING_MODES } from 'jslib-html5-camera-photo';
import './App.css';

// import des sockets
import openSocket from 'socket.io-client';
const socket = openSocket('http://localhost:8000');


class App extends React.Component {
  constructor (props, context) {
    super(props, context);
    this.cameraPhoto = null;
    this.videoRef = React.createRef();
    this.state = {
      dataUri: ''
    }
  }

  componentDidMount () {
    // We need to instantiate CameraPhoto inside componentDidMount because we
    // need the refs.video to get the videoElement so the component has to be
    // mounted.
    this.cameraPhoto = new CameraPhoto(this.videoRef.current);
  }

  startCamera (idealFacingMode, idealResolution) {
    this.cameraPhoto.startCamera(idealFacingMode, idealResolution)
      .then(() => {
        console.log('camera is started !');
      })
      .catch((error) => {
        console.error('Camera not started!', error);
      });
  }

  takePhoto () {
    const config = {
      sizeFactor: 1,
      isImageMirror: true
    };

    let dataUri = this.cameraPhoto.getDataUri(config);
    alert(dataUri);
    this.setState({ dataUri });
    socket.emit('sendImage', dataUri);
  }

  render () {
    return (
      <section className="Cam-container">

        <video className="Cam-videoFlux"
          ref={this.videoRef}
          autoPlay="true"
        />

        <div className="Cam-header">Prenez en photo votre cours !</div>
        <button className="Cam-activate" onClick={ () => {
          let facingMode = FACING_MODES.ENVIRONMENT;
          let idealResolution = { width: 375, height: 667 };
          this.startCamera(facingMode, idealResolution);
        }}> Activer la camera </button>

        <div class="Cam-take-wrapper">
          <div className="Cam-take" onClick={ () => {
            this.takePhoto();
          }}></div>
        </div>

      </section>
    );
  }
}

export default App;
