import React, { Component } from "react";
import ReactDOM from "react-dom";
import './styles/tailwind.css';

var btnTemplate = "px-6 py-4 shadow-xl uppercase font-semibold tracking-wider text-sm outline-none";

/*=================================================================
                  STRUCTURE DES COMPOSANTS
===================================================================

/*---------------------------------------------------------------

                        Composant HEADER

------------------------------------------------------------------*/
function Header(props){
  var logo;
  if(props.logo!=""){
    logo = <img src={props.logo}/>
  }
  return(
    <header className="px-8 py-4 border-t-4 border-indigo-700 shadow bg-white">
      <div>
        {logo}
      </div>
      <span className="uppercase font-semibold tracking-wider text-gray-900 text-sm">{props.text}</span>
    </header>
  )
}

/*---------------------------------------------------------------

                          Boutons

------------------------------------------------------------------*/
function BtnFilled(props){
  return(
    <button
      className={btnTemplate+"bg-white hover:bg-gray-200"}
      onClick={()=>props.changePageOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

function BtnStroked(props){
  return(
    <button
      className="bg-white px-4 uppercase font-semibold tracking-wider text-gray-900 text-sm rounded outline-none"
      onClick={()=>props.changePageOnClick(props.destination)}
    >
      {props.text}
    </button>
  )
}


/*---------------------------------------------------------------

Ici on affiche plusieurs choses différentes selon l'état PAGE.
- Si page vaut home on affiche le menu
- Si page vaut frame360 on affiche la sphere draggable
- Si page vaut frameVR on créé la scene VR

------------------------------------------------------------------*/
class App extends Component {
  constructor(props) {
      super(props)
      // l'état par défaut de la page est home et il n'y a pas d'image sur la page home d'ou id, date et filename nuls
      this.state = {
          page: "home",
      };

      this.changePageOnClick = this.changePageOnClick.bind(this)

  }

  changePageOnClick(data){
      alert("test");
    this.setState({
        page: data
    });
  }

  render() {

    return (
      <section className="bg-gray-100">
          <BtnFilled actionOnClick={this.changePageOnClick} text="button"/>
      </section>
    )
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
