class FrameVR extends Component {
  constructor(props) {
    super(props);

    // on initialise les refs
    this.frameRef = React.createRef();
    this.btnVRRef = React.createRef();

    // on initialise les états
    this.state = {
       room: this.props.room
       };
  }
  componentDidMount() {


    // on récupère la room
    var room = this.state.room;

    // on active le frame
    var returnInit = init(room);

    // on ajoute le renderer
    this.frameRef.appendChild( returnInit[0] );

    // on ajoute le bouton VR enable
    this.buttonVRRef.appendChild( returnInit[1] );

    var camera;
