  		renderer.vr.enabled = true;

  		scene = new THREE.Scene();

  		camera = new THREE.PerspectiveCamera( 90, window.innerWidth / window.innerHeight, 1, 1000 );
  		camera.layers.enable( 1 );

      var geometry = new THREE.SphereBufferGeometry( 500, 60, 40 );
      // invert the geometry on the x-axis so that all of the faces point inward
      geometry.scale( -1, 1, 1 );

      var texture = new THREE.TextureLoader().load( process.env.PUBLIC_URL + '/img/'+room );

      var material = new THREE.MeshBasicMaterial( { map: texture } );

      mesh = new THREE.Mesh( geometry, material );

      scene.add( mesh );

  		window.addEventListener( 'resize', onWindowResize, false );

      // on retourne les éléments à ajouter au dom
  		return [renderer.domElement, WEBVR.createButton( renderer, { referenceSpaceType: 'local' } )];

  	}


  	function onWindowResize() {

  		camera.aspect = window.innerWidth / window.innerHeight;
  		camera.updateProjectionMatrix();

  		renderer.setSize( window.innerWidth, window.innerHeight );

  	}

  	function animate() {

  		renderer.setAnimationLoop( render );

  	}

  	function render() {

  		renderer.render( scene, camera );

  	}



  }


  render() {
    return (
        <section>
          <div className="itemRoom" ref={ref => (this.frameRef = ref)} />
          <div className="vrEnabled" ref={ref => (this.buttonVRRef = ref)}/>
        </section>
    )
  }
}




