        var pageReturn = dataFromChild[0];
        var filenameReturn = dataFromChild[1];
        this.setState({
            page: pageReturn,
            filename: filenameReturn
        });

  }

  render() {

    // on va remplir un tableau d'élément jsx
    var lineRender = [];
    // on scan chaque élément du json
    rooms.forEach((element) => {
      // on récupère la date
      var date = element.date;

      // on rédcupère le sous tableau correspondant à la date
      var content = element.content;

      // on remplit le tableau qui récupère l'information du click de l'enfant
      lineRender.push(<LineNavRooms page={this.childHandler} date={date} content={content}/>);
    });

    // on dispatch selon l'état
    var page = this.state.page;
    if(page=="home"){
      var pageWrapper = <section>
                          <header><span>HomeView | Questembert</span></header>
                          <section className="homeContainer">{lineRender}</section>
                        </section>
    }
    else if(page=="frame360"){
      var pageWrapper = <section className="FrameContainer">
                          <span className="FrameReturn" onClick={()=>this.setState({page:"home"})}>Retour</span>
                          <span className="goVR" onClick={()=>this.setState({page:"frameVR"})}>Voir en VR</span>

                          <Frame360 room={this.state.filename}/>
                        </section>
    }
    else if(page=="frameVR"){
      var pageWrapper = <section className="FrameContainer">
                          <span className="FrameReturn" onClick={()=>this.setState({page:"home"})}>Retour</span>
                          <FrameVR room={this.state.filename}/>
                        </section>
    }

    return (
      <section>

        {pageWrapper}
      </section>
    )
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
