import React, { Component } from "react";
import ReactDOM from "react-dom";
import './styles/tailwind.css';
import {Header,
        BtnMainFilled,
        BtnMainStroked,
        BtnMainPillFilled, 
        BtnMainPillStroked,
        BtnAltFilled,
        BtnAltStroked,
        BtnAltPillFilled,
        BtnAltPillStroked, 
        H1,
        H2,
        H3,
        H4,
        H5,
        H6
        } from './Components';


/*=================================================================
                  STRUCTURE DES COMPOSANTS
===================================================================


/*---------------------------------------------------------------

Ici on affiche plusieurs choses différentes selon l'état PAGE.
- Si page vaut home on affiche le menu
- Si page vaut frame360 on affiche la sphere draggable
- Si page vaut frameVR on créé la scene VR

------------------------------------------------------------------*/
class App extends Component {
  constructor(props) {
      super(props)
      // l'état par défaut de la page est home et il n'y a pas d'image sur la page home d'ou id, date et filename nuls
      this.state = {
          page: "Schoolbag components library",
      };

      this.changePageOnClick = this.changePageOnClick.bind(this)

  }

  changePageOnClick(data){
    this.setState({
        page: data
    });
  }

  render() {

    return (
      <section className="bg-gray-100 h-screen">
        <Header logo="" text={this.state.page}/>
        <section className="w-2/3 mx-auto">
            <H1 text="Buttons"/>
            <hr/>
            <H3 text="Main buttons"/>
            <section className="flex justify-between mb-10">

                <BtnMainFilled actionOnClick={this.changePageOnClick} destination="Page 1" text="Go page1"/>
                <BtnMainStroked actionOnClick={this.changePageOnClick} destination="Page 1" text="Go page1"/>
                <BtnMainPillFilled actionOnClick={this.changePageOnClick} destination="Page 1" text="Go page1"/>
                <BtnMainPillStroked actionOnClick={this.changePageOnClick} destination="Page 1" text="Go page1"/>

            </section>

            <H3 text="Alternative buttons"/>
            <section className="flex justify-between mb-10">
                <BtnAltFilled actionOnClick={this.changePageOnClick} destination="Page 1" text="Go page1"/>
                <BtnAltStroked actionOnClick={this.changePageOnClick} destination="Page 1" text="Go page1"/>
                <BtnAltPillFilled actionOnClick={this.changePageOnClick} destination="Page 1" text="Go page1"/>
                <BtnAltPillStroked actionOnClick={this.changePageOnClick} destination="Page 1" text="Go page1"/>
            </section>

            <H1 text="Headings"/>
            <hr/>

            <H1 text="H1 title"/>
            <H2 text="H2 title"/>
            <H3 text="H3 title"/>
            <H4 text="H4 title"/>
            <H5 text="H5 title"/>
            <H6 text="H6 title"/>

            <H1 text="Headings"/>
            <hr/>

        </section>


      </section>
    )
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
