import React from 'react';
import { sendMessage } from './api';
import logo from './logo.svg';
import './App.css';


class TextSubmit extends React.Component {
  constructor(props){
    super(props)

    this.state = {inputValue: ''};
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }


  handleChange(event) {
    this.setState({inputValue: event.target.value})
  }

  handleSubmit(event) {
    this.props.parentCallback(this.state.inputValue);
    event.preventDefault();
  }

  render() {
    return (
      <div>
        <input type={this.props.typeInput} value={this.state.value} onChange={this.handleChange}/>
        <button onClick={this.handleSubmit}>{this.props.valueSubmit}</button>
      </div>
    );
  }
}
class MicroChat extends React.Component {
  constructor(props){
    super(props)

    this.state = {inputValue: ''};

  }



  callbackFunction = (childData) => {
        this.setState({inputValue: childData});
        sendMessage(childData);
  }

  render() {

    return (
      <div>

        <TextSubmit typeInput="text" valueSubmit="Envoyer" parentCallback = {this.callbackFunction}/>
        <p>{this.state.inputValue}</p>
      </div>
    );
  }
}


export default MicroChat;
