class App extends Component {
  constructor(props) {
      super(props)
      this.state = {
          page: "home"
      };
      this.childHandler = this.childHandler.bind(this)
  }


  // on récupère la room depuis le menu
  childHandler(dataFromChild) {

        this.setState({
            page: dataFromChild
        });
        alert(this.state.page);
  }

  render() {

    // on va remplir un tableau d'élément jsx
    var lineRender = [];
    // on scan chaque élément du json
    rooms.forEach((element) => {
      // on récupère la date
      var date = element.date;
      var content = element.content;
      lineRender.push(<LineNavRooms page={this.childHandler} date={date} content={content}/>);
    });

    if(this.state.page=="home"){
      var pageWrapper = <section className="homeContainer">{lineRender}</section>
    }
    else{
      var pageWrapper = <section className="homeContainer"></section>
    }

    return (
      <section>
        <header><span>HomeView | Questembert</span></header>
        {pageWrapper}
      </section>
    )
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
