import React from 'react';
import { sendMessage } from './api';

import logo from './logo.svg';
import './App.css';


class TextSubmit extends React.Component {
  constructor(props){
    super(props)

    this.state = {inputValue: ''};
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }


  handleChange(event) {
    this.setState({inputValue: event.target.value})
  }

  handleSubmit(event) {
    this.props.parentCallback(sendMessage(this.state.inputValue));
    event.preventDefault();


  }

  render() {
    return (
      <div>
        <input type={this.props.typeInput} value={this.state.value} onChange={this.handleChange}/>
        <button onClick={this.handleSubmit}>{this.props.valueSubmit}</button>
      </div>
    );
  }
}

function Message(props){
  return(
    <div>{props.text}</div>
  )
}

class MicroChat extends React.Component {
  constructor(props){
    super(props)

    this.state = {
      arrayMessage: []
    };

  }

  callbackFunction = (childData) => {
        this.setState(state =>{
          const newArrayMessage = state.arrayMessage.concat(<Message text={childData}/>);
          return newArrayMessage;

        });
  }

  render() {

    return (
      <div>
        <section>
        {this.state.arrayMessage}
        </section>
        <TextSubmit typeInput="text" valueSubmit="Envoyer" parentCallback = {this.callbackFunction}/>
      </div>
    );
  }
}


export default MicroChat;
