import React, { Component } from "react";
import './styles/tailwind.css';


/*---------------------------------------------------------------
            Définition des template de classes communs 
------------------------------------------------------------------*/

var btnTemplate     = "outline-none px-6 py-4  uppercase font-semibold tracking-wider text-sm ";
var titleTemplate   = "uppercase font-semibold tracking-wider my-1 text-gray-900 ";

/*---------------------------------------------------------------
                        Composant HEADER
------------------------------------------------------------------*/
function Header(props){
  var logo;
  if(props.logo!==""){
    logo = <img src={props.logo} alt="brand logo"/>
  }
  return(
    <header className="px-8 py-4 mb-4 border-t-4 border-brandColor-700 shadow bg-white">
      <div>
        {logo}
      </div>
      <span className="uppercase font-semibold tracking-wider text-gray-900 text-sm">{props.text}</span>
    </header>
  )
}

/*---------------------------------------------------------------
                        titles
------------------------------------------------------------------*/
function H1(props){
  return(
    <h1
      className = { titleTemplate + "text-3xl" }>
      {props.children}
    </h1>
  )
}

function H2(props){
  return(
    <h2
      className = { titleTemplate + "text-xl" }>
      {props.children}
    </h2>
  )
}

function H3(props){
  return(
    <h3
      className = { titleTemplate + "text-lg" }>
      {props.children}
    </h3>
  )
}

function H4(props){
  return(
    <h4
      className = { titleTemplate + "text-base" }>
      {props.children}
    </h4>
  )
}

function H5(props){
  return(
    <h1
      className = { titleTemplate + "text-sm" }>
      {props.children}
    </h1>
  )
}

function H6(props){
  return(
    <h1
      className = { titleTemplate + "text-xs" }>
      {props.children}
    </h1>
  )
}

/*---------------------------------------------------------------
                          Buttons
------------------------------------------------------------------*/
function BtnMainFilled(props){
  return(
    <button
      className = { btnTemplate + "bg-brandColor-200 text-brandColor-800 border-4 border-brandColor-200 hover:bg-brandColor-300 rounded hover:border-brandColor-300" }
      onClick   = {()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

// boutton avec fond transparent border 4
function BtnMainStroked(props){
  return(
    <button
      className = { btnTemplate + "bg-transparent text-brandColor-800 border-4 border-brandColor-200 hover:bg-brandColor-200 rounded" }
      onClick   = {()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

function BtnMainPillFilled(props){
  return(
    <button
      className = { btnTemplate + "bg-brandColor-200 text-brandColor-800 border-4 border-brandColor-200 hover:bg-brandColor-300 rounded-full hover:border-brandColor-300" }
      onClick   = {()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

// boutton avec fond transparent border 4
function BtnMainPillStroked(props){
  return(
    <button
      className = { btnTemplate + "bg-transparent text-brandColor-800 border-4 border-brandColor-200 hover:bg-brandColor-200 rounded-full" }
      onClick   = {()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

function BtnAltFilled(props){
  return(
    <button
      className={btnTemplate+"bg-gray-200 hover:bg-gray-300 border-4 border-gray-200 hover:border-gray-300 text-gray-800 rounded"}
      onClick={()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}


function BtnAltStroked(props){
  return(
    <button
      className={btnTemplate+"bg-transparent hover:bg-gray-300 border-4 border-gray-300 text-gray-800 rounded"}
      onClick={()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

function BtnAltPillFilled(props){
  return(
    <button
      className={btnTemplate+"bg-gray-200 hover:bg-gray-300 border-4 border-gray-200 hover:border-gray-300 text-gray-800 rounded-full"}
      onClick={()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}


function BtnAltPillStroked(props){
  return(
    <button
      className={btnTemplate+"bg-transparent hover:bg-gray-300 border-4 border-gray-300 text-gray-800 rounded-full"}
      onClick={()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

/*---------------------------------------------------------------
                          inputs
------------------------------------------------------------------*/
class InputTextMovingLabel extends Component {
    constructor(props) {
      super(props)
      // l'état par défaut de la page est home et il n'y a pas d'image sur la page home d'ou id, date et filename nuls
      this.state = {
          onFocusClasses: '',
      };
    }

    handleFocus(){
        this.setState({
            onFocusClasses: 'mb-7'
        })
    }

    render(){

        return(
        <div className="relative h-16 w-full">
            <input 
                type="text"
                className="pl-2 absolute bottom-0 l-0 h-10 w-full bg-gray-100 focus:bg-gray-200 border-2 border-gray-200 text-gray-800 rounded outline-none"
                required="required"
                onClick={()=>this.handleFocus()}
            />


            <div className={"absolute h-10 bottom-0 l-0 pl-2 flex items-center "+ this.state.onFocusClasses} 
                 style={{"transitionDuration":"0.2s"}}>
                <span className="uppercase tracking-wide text-2xs text-gray-800 font-semibold">{this.props.label}</span>
            </div>
        </div>
        )
    }


}

class InputTextMovingPlaceholder extends Component {
    constructor(props) {
      super(props)
      // l'état par défaut de la page est home et il n'y a pas d'image sur la page home d'ou id, date et filename nuls
      this.state = {
          inputValue: false,
          inputPlaceholder: '',
          labelPlaceholder: '',
          launchAnimation: '',
      };
    }

    handleFocus(){
        this.setState({
            inputPlaceholder: 'hidden',
            labelPlaceholder: this.props.placeholder,
            launchAnimation: 'animateIn'
        })
    }

    handleFocusOut(){
        if(!this.state.inputValue){
            this.setState({
                inputPlaceholder: 'animateIn',
                launchAnimation: 'animateOut'
            })   
        }
    }

    render(){

        return(

        <div className="relative h-16 w-full">
            <input 
                type="text"
                className="pl-2 absolute bottom-0 l-0 h-10 w-full bg-gray-100 focus:bg-gray-200 border-2 border-gray-200 text-gray-800 rounded outline-none"
                required="required"
                onFocus={()=>this.handleFocus()}
                onBlur={()=>this.handleFocusOut()}
                value={this.state.inputValue}
                onChange={evt => this.updateInputValue(evt)}

            />

            <div className="flex absolute top-0 l-0 mt-2 pl-2">
                <span className=" uppercase tracking-wide text-2xs text-gray-800 font-semibold">         
                    {this.props.label}
                </span>
                <span className={"ml-2 uppercase tracking-wide text-2xs text-gray-500 font-semibold opacity-0 "+ this.state.launchAnimation}>{this.state.labelPlaceholder}</span>
            </div>

            <div className={"absolute h-10 bottom-0 l-0 pl-2 flex items-center " + this.state.inputPlaceholder} 
                style={{"transitionDuration":"0.2s"}}>
                <span className="uppercase tracking-wide text-2xs text-gray-500 font-semibold">{this.props.placeholder}</span>
            </div>


        </div>
        )
    }


}

function InputText(props){
  return(
    <div className="relative h-16 w-full">
        <input 
            type="text"
            className="pl-2 absolute bottom-0 l-0 h-10 w-full bg-gray-100 focus:bg-gray-200 border-2 border-gray-200 text-gray-800 rounded outline-none"
            required="required"

        />

        <span className="absolute top-0 l-0 mt-2 pl-2 uppercase tracking-wide text-2xs text-gray-800 font-semibold">         
            {props.label}
        </span>

    </div>
  )
}


/*---------------------------------------------------------------
                          Markdown
------------------------------------------------------------------*/

function Markdown(props){
  return(
    <div className="my-4">
        <code className="text-xs bg-gray-700 p-4 rounded text-gray-100">{props.code}</code>
    </div>
  )
}

/*---------------------------------------------------------------
                          EXPORT
------------------------------------------------------------------*/
export {
    Header,
    BtnMainFilled,
    BtnMainStroked,
    BtnMainPillFilled,
    BtnMainPillStroked,
    BtnAltFilled,
    BtnAltStroked,
    BtnAltPillFilled,
    BtnAltPillStroked,
    H1,
    H2,
    H3,
    H4,
    H5,
    H6,
    InputText,
    InputTextMovingLabel,
    InputTextMovingPlaceholder,
    Markdown
}
