import React, { Component } from "react";
import ReactDOM from "react-dom";
import './styles/tailwind.css';

/*---------------------------------------------------------------
            Définition des brand colors
------------------------------------------------------------------*/
var brandColors = {
    bg: 'bg-red-500',
    border: 'border-red-500',
    text: 'text-red-500',
}

/*---------------------------------------------------------------
            Définition des template de classes communs 
------------------------------------------------------------------*/

var btnTemplate     = "outline-none px-6 py-4 shadow-xl uppercase font-semibold tracking-wider text-sm ";


/*---------------------------------------------------------------
                        Composant HEADER
------------------------------------------------------------------*/
function Header(props){
  var logo;
  if(props.logo!=""){
    logo = <img src={props.logo}/>
  }
  return(
    <header className="px-8 py-4 mb-4 border-t-4 border-indigo-700 shadow bg-white">
      <div>
        {logo}
      </div>
      <span className="uppercase font-semibold tracking-wider text-gray-900 text-sm">{props.text}</span>
    </header>
  )
}

/*---------------------------------------------------------------
                          Boutons
------------------------------------------------------------------*/
function BtnMainFilled(props){
  return(
    <button
      className = { btnTemplate + "bg-brandColor hover:bg-brandColorHover rounded" }
      onClick   = {()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

// boutton avec fond transparent border 4
function BtnMainStroked(props){
  return(
    <button
      className = { btnTemplate + "bg-transparent text-brandColor border-4 border-brandColor hover:bg-brandColor hover:text-white rounded" }
      onClick   = {()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}

function BtnAltFilled(props){
  return(
    <button
      className={btnTemplate+"bg-white hover:bg-gray-200 rounded"}
      onClick={()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}


function BtnAltStroked(props){
  return(
    <button
      className={btnTemplate+"bg-transparent hover:bg-gray-200 rounded"}
      onClick={()=>props.actionOnClick(props.destination)}>
      {props.text}
    </button>
  )
}


export {
    Header,
    BtnMainFilled,
    BtnMainStroked
}
