import React, { Component } from "react";
import ReactDOM from "react-dom";
import * as THREE from "three";
import { WEBVR } from 'three/examples/jsm/vr/WebVR.js';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      frame: '',
      renderer: ''};
  }
  componentDidMount() {

    //this.setState({ renderer: WEBVR.createButton( renderer, { referenceSpaceType: 'local' } ) });
    // on ajoute l'élément au composant react

    var camera, scene, renderer;

		var isUserInteracting = false,
				onMouseDownMouseX = 0, onMouseDownMouseY = 0,
				lon = 0, onMouseDownLon = 0,
				lat = 0, onMouseDownLat = 0,
				phi = 0, theta = 0;



    //this.setState({frame: frame});



    var scene = new THREE.Scene();
		var camera = new THREE.PerspectiveCamera( 75, window.innerWidth/window.innerHeight, 0.1, 1000 );

		var renderer = new THREE.WebGLRenderer();
		renderer.setSize( window.innerWidth, window.innerHeight );

    this.mount.appendChild( renderer.domElement );

    var geometry = new THREE.SphereBufferGeometry( 5, 1, 1 );
    // invert the geometry on the x-axis so that all of the faces point inward
    geometry.scale( -1, 1, 1 );

    var texture = new THREE.TextureLoader().load( './img/IMG_7233.JPG' );

    var material = new THREE.MeshBasicMaterial( {color: 0x00ff00, specular: 0xffffff,
    shininess: 20, map: texture} );
    var mesh = new THREE.Mesh( geometry, material );


		scene.add( mesh );

		camera.position.z = 5;

		var animate = function () {
			requestAnimationFrame( animate );

			//mesh.rotation.x += 0.01;
			//mesh.rotation.y += 0.01;

			renderer.render( scene, camera );
		};

		animate();

  }


  render() {
    return (
      <div>
        <div ref={ref => (this.mount = ref)} />

      </div>
    )
  }
}
const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
