import React, { Component } from "react";
import ReactDOM from "react-dom";
import * as THREE from "three";
import { WEBVR } from 'three/examples/jsm/vr/WebVR.js';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      frameVR:'',
      buttonVR: ''};
  }
  componentDidMount() {
    var scene = new THREE.Scene();
    var camera = new THREE.PerspectiveCamera( 75, window.innerWidth/window.innerHeight, 0.1, 1000 );
    var renderer = new THREE.WebGLRenderer();
    renderer.setSize( window.innerWidth/2, window.innerHeight/2 );

    // on ajoute le bouton en tant qu'état
    this.setState({ buttonVR: WEBVR.createButton( renderer )});
    this.setState({frameVR: renderer.domElement });
    // on ajoute l'élément au composant react
    this.mount.appendChild( renderer.domElement );

    // creation de la 3D
    var light = new THREE.DirectionalLight( 0xffffff );
    light.position.set( 0, 1, 1 ).normalize();

    var geometry = new THREE.BoxGeometry( 1, 1, 1 );
    var material = new THREE.MeshPhongMaterial( { ambient: 0x050505, color: 0xFF0000, specular: 0x555555, shininess: 30, wireframe: true } );
    var cube = new THREE.Mesh( geometry, material );

    // on ajoute les éléments à la scene
    scene.add( cube );
    scene.add( light );
    // on gere la position de la camera
    camera.position.z = 5;
    var animate = function () {
      requestAnimationFrame( animate );
      cube.rotation.x += 0.01;
      cube.rotation.y += 0.01;
      renderer.render( scene, camera );
    };
    animate();
  }
  render() {
    return (
      <div>
        <div ref={ref => (this.mount = ref)} />
        <button>{ this.state.buttonVR }</button>
      </div>
    )
  }
}
const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
