import React from 'react';
import { sendMessage, getMessages, sendPseudo, getPseudo } from './api';

import './App.css';


class TextSubmit extends React.Component {
  constructor(props){
    super(props)

    this.state = {
      inputValue: '',
      placeholder: 'Entrez votre pseudo'
  };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({inputValue: event.target.value})
  }

  handleSubmit(event) {
    const purpose = this.props.purpose;
    let className;
    if(purpose=='start'){

      sendPseudo(this.state.inputValue);

      // on remet l'input à valeure vide
      document.getElementById('inputText').value = ''

      // on change le placeholder
      this.setState({placeholder: "Ecrivez votre message"})
    }
    else{

      sendMessage(this.state.inputValue);

      // on remet l'input à valeure vide
      document.getElementById('inputText').value = ''
    }

    //this.props.parentCallback(this.state.inputValue);
    event.preventDefault();
  }

  render() {
    return (
      <div className="MChat-textSubmit">
        <input id="inputText" type={this.props.typeInput} value={this.state.value} onChange={this.handleChange} placeholder={this.state.placeholder}/>
        <button onClick={this.handleSubmit}>{this.props.valueSubmit}</button>
      </div>
    );
  }
}

function Message(props){
  const letter = props.user.substring(0,1);
  props.isItMe;
  if(props.isItMe){
    return(
      <div className="MChat-message-wraper-right"><span className="MChat-message">{props.text}</span><span className="MChat-user">{letter}</span></div>
    )
  }
  else{
    return(
      <div className="MChat-message-wraper-left"><span className="MChat-user">{letter}</span><span className="MChat-message">{props.text}</span></div>
    )
  }

}

class MicroChat extends React.Component {
  constructor(props){
    super(props)
    // on set un tableau vide
    this.state = {
      arrayMessage: [],
      purpose: 'start',
      pseudo: ''
    };

    // on met chaque nouveau message dans le tableau
    getPseudo((err, pseudo) => {
      document.getElementById('newcomer').append(pseudo+' est connecté !');
      this.setState({
        purpose: 'conv',
        pseudo: pseudo
      })
    });
    // on met chaque nouveau message dans le tableau
    getMessages((err, retour) => {

      const message = retour.message;
      const user = retour.pseudo;

      const isItMe = false;
      if(user==this.state.pseudo){
          isItMe = true;
      }

      const newArray = this.state.arrayMessage.concat(
        <Message user={user} text={message} isItMe={isItMe}/>
      );
      this.setState({arrayMessage: newArray})
    });

  }


  render() {

    return (
      <section className="MChat-container">
        <div className="MChat-content-message">
          <span id="newcomer"></span>
          {this.state.arrayMessage}
        </div>
        <TextSubmit purpose={this.state.purpose} typeInput="text" valueSubmit="Envoyer" parentCallback = {this.callbackFunction}/>
      </section>
    );
  }
}


export default MicroChat;
